[{'body': '验收环境：https://myshell-web-react-git-dev-bug.myshell.fun/zh/explore'}, {'body': '{
  "id": "voice_widget_template",
  "initial": "home_state",
  "states": {
    "home_state": {
      "render": {
        "text": "choose display error type",
        "buttons": [
          {
            "content": "Engine Error",
            "description": "",
            "on_click": "ee"
          },
          {
            "content": "Component Error",
            "description": "",
            "on_click": "ce"
          },
          {
            "content": "LLM Token too long Error",
            "description": "",
            "on_click": "le"
          }
        ]
      },
      "transitions": {
        "ce": "ce",
        "ee": "ee",
        "le": "le"
      }
    },
    "ee": {
      "render": {
        "text": "{{llm_result.123}}" // it's a string of user input text.
      }
    },
    "le": {
      "tasks": [
        {
          "name": "llm_widget_example_task",
          "module_type": "AnyWidgetModule",
          "module_config": {
            "widget_id": "1744214024104448199",
            "user_prompt": {
              "text": "hihi",
            }, // the text inputted into prompt widget, you can get it from user input or upper state
            "system_prompt": "Act as ...", // Optional field. You can input system prompt of bot.
            "top_p": 0.5, // Optional field. Default value is 0.5
            "temperature": 0.5, // Optional field. Default value is 0.5
            "frequency_penalty": 0, // Optional field. Default value is 0
            "presence_penalty": 0, // Optional field. Default value is 0
            "max_tokens": 1024, // Optional field. Default value is 1024
            "memory": "{{[]}}", // Optional field. Default value is empty array. You can fill memory in it like OpenAi Api
            "output_name": "result"
          }
        }
      ]
    },
    "ce": {
      "inputs": {
        "read_text": {
          "type": "text",
          "user_input": true
        }
      },
      "tasks": [
        {
          "name": "llm_widget_example_task",
          "module_type": "AnyWidgetModule",
          "module_config": {
            "widget_id": "1744214024104448199",
            "user_prompt": {
              "text": "hi"
            }, // the text inputted into prompt widget, you can get it from user input or upper state
            "system_prompt": "Act as ...", // Optional field. You can input system prompt of bot.
            "top_p": 0.5, // Optional field. Default value is 0.5
            "temperature": 0.5, // Optional field. Default value is 0.5
            "frequency_penalty": 0, // Optional field. Default value is 0
            "presence_penalty": 0, // Optional field. Default value is 0
            "max_tokens": 1024, // Optional field. Default value is 1024
            "memory": "{{[]}}", // Optional field. Default value is empty array. You can fill memory in it like OpenAi Api
            "output_name": "llm_result"
          }
        },
        {
          "name": "tts_widget_example_task",
          "module_type": "AnyWidgetModule",
          "module_config": {
            "widget_id": "1744008029948350464",
            "content": "", // the text to be read by voice widget, you can get it from user input or upper state
            "output_name": "result"
          }
        }
      ],
      "render": {
        "text": "{{llm_result}}", // it's a string of user input text.
        "audio": "{{result}}", // it's a url produced by voice widget.
        "buttons": [
          {
            "content": "Listen Again",
            "description": "",
            "on_click": "listen"
          }
        ]
      },
      "transitions": {
        "listen": "home_state",
        "CHAT": "home_state"
      }
    }
  }
}'}, {'body': '<img width="1354" src="https://api.zenhub.com/attachedFiles/eyJfcmFpbHMiOnsibWVzc2FnZSI6IkJBaHBBeVhSQlE9PSIsImV4cCI6bnVsbCwicHVyIjoiYmxvYl9pZCJ9fQ==--332060979abeb6666459e359072728d5a4b892d0/image.png" alt="image.png" />
'}]
